import React, { useState, useEffect }from "react";
import {Button, Container, Nav, Navbar,Row,Col} from 'react-bootstrap'
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

import MyNavbar from "./navbar";

function Home(props) {
    const navigate = useNavigate();
    const params = useParams();
    
    useEffect(() => {
        axios.get('http://localhost:8080/data2') 
          .then(res => {
            let copy = [...res.data]
            props.setData1(copy);
            console.log(props.data1); 
          })
          .catch(err => {
            console.error(err);
          });
      }, []);

      const handleComplete = (id) => {
        axios
          .post(`http://localhost:8080/modify/${id}`, {})
          .then((response) => {
            // 데이터를 업데이트하고 다시 렌더링
            const updatedData = props.data1.map((item) => {
              if (item._id === id) {
                return { ...item, complete: true };
              }
              return item;
            });
            props.setData1(updatedData);
            console.log(response);
          })
          .catch((error) => {
            console.log(error);
          });
      };

      const handleDelete = (id,i) => {
        axios.delete(`http://localhost:8080/delete/${id}`,{
        })
            .then(response => {
            let copy2 = [...props.data1]
            copy2.splice(i,1)
            props.setData1(copy2)
            console.log(response);
            })
            .catch(error => {              
            console.error(error);
            })
      };



    return (
        
        <div >
            <MyNavbar></MyNavbar>
            <Container className="large-square">
            <div className="completed-box textst"> {props.user}'s Completed Todo  </div>
            
            {

                props.data1.map((a,i) => {
                    let x = a._id.toString()
                    if(a.complete == false) {
                    return (
                        <Container style={{ display: 'flex' }}  key={a._id}>

                                <div className="small-square" >
                                <div className="top-left textst" onClick={() => {
                                    navigate('/')
                                }}>{a.date}</div>

                                
                                <Link to={`/detail/${x}`} className="bottom-left textst">{a.title}</Link>

                                <Button className="bottom-right-completed" onClick={() => {

                                    /* axios.post(`http://localhost:8080/modify/${a._id}`, {
                                    })
                                    .then(response => {
                                        let copy3 = [...props.data1]
                                        props.setData1(copy3)
                                        console.log(response)
                                    })
                                    .catch(error => {
                                        console.log(error);
                                    }); */
                                    handleComplete(a._id)
                                }}> Complete! </Button>


                                <Button className="bottom-right-delete" variant="danger" 
                                onClick={(e) => {
                                    handleDelete(a._id,i)
                                }}
                                > Delete </Button>
                            </div>
                         </Container>
                    )}

                    else {
                        return (
                            <Container style={{ display: 'flex'}}  key={a._id}>
                                <div className="small-square2 textst" >  {a.date}   {a.title} 
                                <Button className="bottom-right-delete" variant="danger" 
                                style={{ display: 'inline',marginLeft: 250, marginTop:1}} 
                                onClick={(e) => {
                                    handleDelete(a._id,i)
                                }}
                                > Delete </Button>
                                </div>

                            </Container>
                        )
                    }
                })

                
            }

            
            </Container>
            
           
        </div>
    )
}

export default Home;
