                                            let copy2 = [...props.data1]
                                            copy2.splice(i,1)
                                            props.setData1(copy2)
                                            console.log(response);
                                        })
                                        .catch(error => {
                                            
                                            console.error(error);
                                        })
                                    
                                }}
                                > Delete </Button>
                            </div>

                            <div className="small-square2">asdㅁㄴㅇㄴㅁㅇㅁㄴㅇㅁㄴㅇㅁㄴㅇㄴㅁㅇㅁㄴ</div>


                         </Container>


                    )
                })
            }

            

            
           
        </div>
    )
}

export default Home;
