import React , { useState, useEffect } from "react";
import {Button, Container, Nav, Navbar,Row,Col} from 'react-bootstrap'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Add() {
    const navigate = useNavigate();

    let [title, setTitle] = useState("");
    let [content, setContent] = useState("");

    return (
        <div>
            <Navbar>
                <Container>
                    <Row>
                        <Col>
                            <Button variant="contained" color="primary" className="textst"
                                onClick={() => {
                                    navigate('/home')
                                }}>
                                Todolist
                            </Button>
                        </Col>
                        <Col xs="auto">
                            <Button variant="contained" color="primary" className="textst"
                                onClick={() => {
                                    navigate('/add')
                                }}>
                                Add
                            </Button>
                        </Col>
                    </Row>
                </Container>
            </Navbar>

            <Container>
                <div className="sqaure">

                    <div className="textst" style={{ marginTop: 20 }}> Title </div>
                        <input
                            className="input-field"
                            onChange={(e) => {
                                setTitle(e.target.value)
                            }}
                            placeholder="아이디"
                            type="text"
                        />

                    <div className="textst" style={{ marginTop: 20 }}>Content</div>
                        <textarea
                            className="textarea-field"
                            onChange={(e) => {
                                setContent(e.target.value)
                            }}
                            placeholder="긴 문단을 입력하세요"
                        ></textarea>

                        <Button className="add-button" style={{marginTop:20, color: "Dark"}} 
                        onClick={() => {
                            axios.post('http://localhost:8080/data2', {
                                title : title,
                                content : content
                            })
                            .then(response => {
                                navigate('/home')

                            })
                            .catch(error => {
                            });
                                    }}>
                            Add
                        </Button>
                    </div>

                    
                

            </Container>



           
        </div>
    )
}

export default Add;
