import React,{ useState, useEffect } from "react";
import '../App.css';
import {Button, Container, Nav, Navbar} from 'react-bootstrap'
import axios from 'axios';

function Login() {

    let [modal, setModal] = useState(false);
    let [id, setId] = useState("");
    let [pwd, setPwd] = useState("");

    

    return (
        <div className="background">
            <div style={{ fontFamily : "Oswald", fontSize: '100px', color: 'black' }} className="center"> Welcome!</div> 

            <div style={{textAlign : "center"}}>
            <button class="login-button" onClick={ () => {
                setModal(!modal);
            }}>로그인</button>
            
            <button class="signup-button">회원가입</button>

            {
                modal === true ? <Modal id = {id} setId = {setId} setPwd = {setPwd} pwd = {pwd} />: null
            }
            </div>
            
            

            
        </div>
    )
}

function Modal(props){
    return (
        <div>
            {/* <div className="login-form">
            <input type="text" name="id" placeholder="아이디 입력"></input>
            </div>

            <div className="login-form">
            <input type="password" name="pw" placeholder="비밀번호 입력"></input>
            </div>

            <div>
		    <input type="submit" value="제출"></input>
	        </div> */}

            <input 
            _onChange={(e) => {
              props.setId(e.target.value);
            }}
            width="380px"
            height="45px"
            placeholder="아이디"
          />

          <input 
            _onChange={(e) => {
              props.setPwd(e.target.value);
            }}
            width="380px"
            height="45px"
            margin-top = "10000px"
            placeholder="비밀번호"
            type="password"
          />
            
            
        </div>
    )
  }

export default Login;
