import React, { useState, useEffect }from "react";
import {Button, Container, Nav, Navbar,Row,Col} from 'react-bootstrap'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Home() {
    const navigate = useNavigate();
    const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/data2') // API 엔드포인트를 사용자의 서버 주소로 대체
      .then(response => {
        setData(response.data);
        console.log(data)
      })
      .catch(error => {
        console.error(error);
      });
    }, []);

    return (
        <div>
            <Navbar>

                <Container>
                    <Row>
                        <Col>
                            <Button variant="contained" color="primary" className="textst"
                                onClick={() => {
                                    navigate('/home')
                                }}>
                                Todolist
                            </Button>
                        </Col>
                        <Col xs="auto">
                            <Button variant="contained" color="primary" className="textst"
                                onClick={() => {
                                    navigate('/add')
                                }}>
                                Add
                            </Button>
                        </Col>
                    </Row>
                </Container>

                <div>
                    {data.map((item) => (
                        <div key={item._id}>
                        <h2>{item.title}</h2>
                        <p>{item.content}</p>
                        </div>
                    ))}
                </div>




            </Navbar>
           
        </div>
    )
}

export default Home;
